mapper.select:
SELECT 

FROM t_user a

WHERE a.id = #{id}

SELECT 

FROM t_user a




		AND a.account = #{account}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.dealer_id = #{dealerId}


		AND a.audit_status = #{auditStatus}


		AND a.create_type = #{createType}


		AND a.input_flag = #{inputFlag}




		ORDER BY ${page.orderBy}

SELECT 

FROM t_user a






		ORDER BY ${page.orderBy}
