mapper.select:
SELECT 

FROM t_dealer a

WHERE a.id = #{id}

SELECT 

FROM t_dealer a




		AND a.code = #{code}


		AND a.is_record_integral = #{isRecordIntegral}


		AND a.original_code = #{originalCode}


		AND a.market = #{market}


		AND a.name LIKE 
			'%'||#{name}||'%'
			'%'+#{name}+'%'
			concat('%',#{name},'%')


		AND a.status = #{status}




		ORDER BY ${page.orderBy}

SELECT 

FROM t_dealer a






		ORDER BY ${page.orderBy}
