mapper.select:
SELECT 

FROM t_report_standbook a

WHERE a.id = #{id}

with grade as ( SELECT b.id , STUFF(( SELECT ','+ name+':'+convert(varchar(1),grade) FROM (SELECT d.id, d.grade, d.report_id, d.grade_id, g.name FROM t_report_standbook_grade_detail d LEFT OUTER JOIN t_grade g ON d.grade_id = g.id) a WHERE b.id = a.report_id FOR XML PATH('')),1 ,1, '') grades FROM t_report_standbook b GROUP BY b.id ) SELECT a.id as id, CONVERT(VARCHAR(10),a.create_date,120) createDate2, CONVERT(VARCHAR(12),a.create_date,108 ) createTime, CONVERT(VARCHAR(10),a.operate_date,120) operateDate2, a.hospital_name as hospitalName, a.province_name as provinceName, a.city_name as cityName, a.doctor_name as doctorName, case a.patient_sex when '1' then '男' else '女' end patientSex, a.patient_age as patientAge, a.surgery_grade as surgeryGrade, a.user_name, a.baseReportName, a.dealerName as dealerName2, case a.type when '1' then '直属/子公司销售员报台' else '经销商报台' end type, case pd.is_record_unit when '1' then 1 else 0 end isRecordUnit, a.suggest, g.grades as comments2, case a.status when '1' then '启用' else '禁用' end status, pd.materialCode, pd.materialDesc, pd.materialSpeDesc, pd.seriesName, pd.individualcode, pd.dealerCode, pd.dealerName, pd.saleType, pd.comments FROM t_report_standbook a
LEFT OUTER JOIN t_report_standbook_product_detail pd on a.id = pd.report_id
LEFT OUTER JOIN grade g ON a.id = g.id
AND a.operate_date BETWEEN #{beginOperateDate} AND #{endOperateDate}
AND a.hospital_name = #{hospitalName}
AND a.province_name = #{provinceName}
AND a.city_name = #{cityName}
AND a.doctor_name = #{doctorName}
AND CONVERT(VARCHAR(10),a.create_date,120) >= #{beginCreateDate}
AND CONVERT(VARCHAR(10),a.create_date,120) <= #{endCreateDate}
AND a.status = #{status}
AND a.user_name = #{userName}
AND d.name = #{dealerName}

SELECT



FROM t_report_standbook a




		AND a.operate_date BETWEEN #{beginOperateDate} AND #{endOperateDate}


		AND a.hospital_name = #{hospitalName}


		AND a.province_name = #{provinceName}


		AND a.city_name = #{cityName}


		AND a.doctor_name = #{doctorName}


		AND CONVERT(VARCHAR(10),a.create_date,120) >= #{beginCreateDate}


		AND CONVERT(VARCHAR(10),a.create_date,120) <= #{endCreateDate}




		AND a.status = #{status}


		AND a.user_name = #{userName}


		AND d.name = #{dealerName}




		ORDER BY ${page.orderBy}


		ORDER BY a.create_date DESC

SELECT
a.id AS "id", a.operate_date AS "operateDate", a.hospital_name AS "hospitalName", a.hospital_id AS "hospitalId", a.province_code AS "provinceCode", a.province_name AS "provinceName", a.city_name AS "cityName", a.city_code AS "cityCode", a.doctor_name AS "doctorName", a.patient_sex AS "patientSex", a.patient_age AS "patientAge", a.surgery_id AS "surgeryId", a.user_name AS "userName", a.user_id AS "userId", a.type AS "type", a.unit_count AS "unitCount", a.create_date AS "createDate", a.status AS "status", a.suggest AS "suggest", a.update_date AS "updateDate", a.update_admin_name AS "updateAdminName", d.name AS "dealerName",
a.patient_name AS "patientName" FROM t_report_standbook a
AND a.operate_date BETWEEN #{beginOperateDate} AND #{endOperateDate}
AND a.hospital_name = #{hospitalName}
AND a.province_name = #{provinceName}
AND a.city_name = #{cityName}
AND a.doctor_name = #{doctorName}
AND a.create_date BETWEEN #{beginCreateDate} AND #{endCreateDate}
AND a.status = #{status}
AND a.user_name = #{userName}
AND d.name = #{dealerName}
ORDER BY ${page.orderBy}
ORDER BY a.create_date DESC

mapper.update:
UPDATE t_report_standbook SET operate_date = #{operateDate}, hospital_name = #{hospitalName}, hospital_id = #{hospitalId}, province_code = #{provinceCode}, province_name = #{provinceName}, city_name = #{cityName}, city_code = #{cityCode}, doctor_name = #{doctorName}, patient_sex = #{patientSex}, patient_age = #{patientAge}, surgery_id = #{surgeryId}, surgery_grade = #{surgeryGrade},
unit_count = #{unitCount}, status = #{status}, suggest = #{suggest}, update_date = #{updateDate}
WHERE id = #{id}

mapper.select:
SELECT id, CONVERT(VARCHAR(10),operate_date,120) operateDate, hospital_id AS "hospitalId", hospital_name as hospitalName, province_code AS "provinceCode", province_name AS "provinceName", city_name AS "cityName", city_code AS "cityCode", doctor_name AS "doctorName", patient_sex AS "patientSex", patient_age AS "patientAge", surgery_id AS "surgeryId", user_name AS "userName", user_id AS "userId", case status when '1' then '启用' else '禁用' end status, type, unit_count AS "unitCount", suggest, CONVERT(VARCHAR(100),create_date,20) AS "createDate" FROM t_report_standbook WHERE status = '1'
AND user_id = #{userId}
AND CONVERT(VARCHAR(10),operate_date,120) >= #{beginOperateDate}
AND CONVERT(VARCHAR(10),operate_date,120) <= #{endOperateDate}
AND hospital_name LIKE '%'+#{hospitalName}+'%'
AND doctor_name LIKE '%'+#{doctorName}+'%'
AND surgery_id = #{surgeryId}
ORDER BY create_date desc
